mapper.update:
UPDATE book set name = #{name}, kind = #{kind},
                publish = #{publish}, author = #{author}, price = #{price},
                num = #{num}, nnum = #{nnum},description = #{description} where id = #{id}

mapper.select:
select * from book where 1=1

    and name like concat(concat('%',#{name}),'%')

limit #{start},#{end}

